package Exercicios;

import java.util.Scanner;

public class LacoRepeticao4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		/*4 - Obtenha um número digitado pelo usuário e repita a operação de multiplicar ele 
		 * por três (imprimindo o novo valor) até que ele seja maior do que 100. Ex.: se
		 *  o usuário digita 5, deveremos observar na tela a seguinte sequência: 5 15 45 135.*/
		
		int num = 1;
		
		Scanner leia = new Scanner(System.in);
		
		System.out.println("\nEscolha um número inteiro:");
		num = leia.nextInt();
		
		int soma = 0,media = 0, conta = 0;
		
		
		while(num < 100 )
		{
			num = num * 3;
			System.out.println(num);
			
		}
	}		
				
		
}		


